# 色の定義
ORANGE = "\e[38;5;208m"
GREEN = "\e[38;5;34m"
RESET = "\e[0m"

# 葉っぱとみかん本体を色分けして表示するバージョン
def colored_mikan(data)
  colored = ""
  lines = data.split("\n")
  
  lines.each do |line|
    current_color = nil
    line.chars.each_with_index do |char, i|
      if char == '2'  # 葉っぱ
        if current_color != GREEN
          colored << RESET if current_color
          colored << GREEN
          current_color = GREEN
        end
        colored << "*"  # 葉っぱの表示文字
      elsif char == '1'  # みかん本体
        if current_color != ORANGE
          colored << RESET if current_color
          colored << ORANGE
          current_color = ORANGE
        end
        colored << "*"  # みかん本体の表示文字
      else  # 背景
        colored << " "  # 背景は空白
      end
    end
    colored << RESET if current_color
    colored << "\n"
  end
  
  colored
end

# みかんイメージを準備
colored_output = colored_mikan(mikan)

# アニメーションループ
1000.times do |i|
  # 画面クリア
  puts "\e[2J"
  
  # 正弦波でみかんを上下に動かす
  y_pos = 10 + (Math.sin(i * 0.5) * 5).to_i
  
  # カーソル位置設定
  puts "\e[#{y_pos};10H"
  
  # みかんを表示
  puts colored_output
  
  # アニメーションの速度調整
  sleep 0.1
end
